		--* error	local E, C, L, _ = select(2, shCore()):unpack()	local f, e, ncErrorDB = CreateFrame('frame'), 'No error yet.', {		--* filters		['Inventory is full.'] = true,	}	f:SetScript('OnEvent', function(self, event, error)		if ncErrorDB[error] then			UIErrorsFrame:AddMessage(error, 1, 0, 0)		else			e = error		end	end);		SLASH_NCERROR1 = '/error'		function SlashCmdList.NCERROR() E.Suitag(e) end	UIErrorsFrame:UnregisterEvent('UI_ERROR_MESSAGE')	f:RegisterEvent('UI_ERROR_MESSAGE')